import { useState, useEffect } from 'react';
import axios from 'axios';

const useHomeTimeLineTweets = (isAuthenticated) => {
    const [homeTimeLineTweets, setHomeTimeLineTweets] = useState(null);

    useEffect(() => {
        if (isAuthenticated) {
            const getHomeTimeLineTweets = async () => {
                try {
                    const resultFromGettingTweets = await axios({
                        url: "http://localhost:4000/home-timeline-tweets",
                        method: "GET",
                        withCredentials: "include",
                        headers: {
                          Accept: "application/json",
                          "Content-Type": "application/json",
                          "Access-Control-Allow-Credentials": true
                        }
                    });

                    setHomeTimeLineTweets(resultFromGettingTweets.data.homeTimeLineTweets);
                } catch (err) {
                    console.log("Error getting tweets", err);
                    setHomeTimeLineTweets(null);
                }
            };

            getHomeTimeLineTweets();
        }
    }, []);

    return {
        homeTimeLineTweets,
        setHomeTimeLineTweets,
    };
};

export default useHomeTimeLineTweets;
