import React from 'react';
import logo from './logo.svg';
import './App.css';
import crypto from 'crypto';
import v4 from './lib/aws-signature-v4';
import audioUtils from './lib/audioUtils';
import mic from 'microphone-stream';

// import marshaller from '@aws-sdk/eventstream-marshaller';
// import util_utf8_node from '@aws-sdk/util-utf8-node';

const marshaller = require("@aws-sdk/eventstream-marshaller"); // for converting binary event stream messages to and from JSON
const util_utf8_node = require("@aws-sdk/util-utf8-node");

const AudioContext = window.AudioContext || window.webkitAudioContext;
const context = new AudioContext();
const key = process.env.KEY;
const secret_key = process.env.SECRET_KEY;
const session_token = process.env.SESSION_TOKEN;
const region = process.env.REGION;
const language = process.env.LANGUAGE;
const sampleRate = process.env.SAMPLE_RATE;

let socket;
let micStream;
let inputSampleRate;

const eventStreamMarshaller = new marshaller.EventStreamMarshaller(util_utf8_node.toUtf8, util_utf8_node.fromUtf8);

const websocket = new WebSocket('ws://localhost:8000'); //put url

websocket.onopen = () => {
  console.log("Connected");
};

websocket.onerror = () => {
  console.log("Error!!!!!!!!!")
}

websocket.onmessage = (message) => {
  console.log("MESSAGE", message);
};

const getAudioEventMessage = (buffer) => {
  // wrap the audio data in a JSON envelope
  return {
      headers: {
          ':message-type': {
              type: 'string',
              value: 'event'
          },
          ':event-type': {
              type: 'string',
              value: 'AudioEvent'
          }
      },
      body: buffer
  };
};

function convertAudioToBinaryMessage(audioChunk) {
  let raw = mic.toRaw(audioChunk);

  if (raw == null)
      return;

  // downsample and convert the raw audio bytes to PCM
  let downsampledBuffer = audioUtils.downsampleBuffer(raw, inputSampleRate, sampleRate);
  let pcmEncodedBuffer = audioUtils.pcmEncode(downsampledBuffer);

  // add the right JSON headers and structure to the message
  let audioEventMessage = getAudioEventMessage(Buffer.from(pcmEncodedBuffer));

  //convert the JSON object + headers into a binary event stream message
  let binary = eventStreamMarshaller.marshall(audioEventMessage);

  return binary;
};

const handleListen = () => {
  navigator.mediaDevices.getUserMedia({
    audio: true,
  }).then((stream) => {
    console.log("STREAm!!!", stream)
    micStream = new mic();

    micStream.on("format", function(data) {
        inputSampleRate = data.sampleRate;
    });

    micStream.setStream(stream);

    micStream.on('data', function(rawAudioChunk) {
      // the audio stream is raw audio bytes. Transcribe expects PCM with additional metadata, encoded as binary
      console.log("DATA", rawAudioChunk);
      //let binary = convertAudioToBinaryMessage(rawAudioChunk);
      let binary = convertAudioToBinaryMessage(rawAudioChunk);
      console.log("Binary", binary);

      websocket.send(binary);
      // if (socket.readyState === socket.OPEN)
      //     socket.send(binary);
    })    
  })
  .catch((err) => {
    console.log("error", err);
  })
};

function App() {
  handleListen();
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
