import React from 'react';
import logo from './logo.svg';
import './App.css';

var AudioContext = window.AudioContext || window.webkitAudioContext;
var context = new AudioContext();

function myPCMSource() { 
  return Math.random() * 2 - 3;
}

function process_microphone_buffer(event) {

  var i, N, inp, microphone_output_buffer;

  microphone_output_buffer = event.inputBuffer.getChannelData(0); // just mono - 1 channel for now
}

const handleListen = () => {
  navigator.mediaDevices.getUserMedia({
    audio: true,
  }).then((stream) => {
    console.log("STREAm!!!", stream)
    var gain_node = audioContext.createGain();
    gain_node.connect( audioContext.destination );

    const microphone = context.createMediaStreamSource(stream);
    console.log("microphone", microphone)
    microphone.connect(gain_node);

    const node = context.createScriptProcessor(16384, 1, 1);
    // listen to the audio data, and record into the buffer
    microphone.connect(node);
    node.connect(microphone.context.destination)
    node.onaudioprocess = process_microphone_buffer;

    // function(e){
    //   // console.log("Input", e.inputBuffer.getChannelData(0))
    //   // console.log("1", e.outputBuffer.getChannelData(0))

    //   // var input_int16 = e.inputBuffer.getChannelData(0);
    //   // var output_float = e.outputBuffer.getChannelData(0);
    //   // for (var i = 0; i < 4096; i++) {
    //   //   output_float[i] = (input_int16[i] / 32767);
    //   // }
    //   // //console.log("e!", e);
    //   //ws.send(JSON.stringify({sound: _arrayBufferToBase64(convertoFloat32ToInt16(inputData))}));
    // }
  })
  .catch((err) => {
    console.log("error", err);
  })
};

function App() {
  handleListen();
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
